import React, { useState, useEffect } from 'react';
import { 
  FaTable,
  FaSync,
  FaTimesCircle,
  FaMap,
  FaTachometerAlt,
  FaChartBar,
  FaUserCog,
  FaBars,
  FaTimes
} from 'react-icons/fa';

// Navigation Component
const Navigation = ({ activeTab, setActiveTab, isMobileMenuOpen, setIsMobileMenuOpen }) => {
  const navItems = [
    { id: 'dashboard', label: 'Dashboard', icon: FaTachometerAlt },
    { id: 'map', label: 'Map', icon: FaMap },
    { id: 'reports', label: 'Reports', icon: FaChartBar },
    { id: 'admin', label: 'Admin', icon: FaUserCog }
  ];

  return (
    <>
      {/* Mobile Menu Button */}
      <button 
        className="mobile-menu-toggle"
        onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}
      >
        {isMobileMenuOpen ? <FaTimes /> : <FaBars />}
      </button>

      {/* Navigation Sidebar */}
      <nav className={`navigation ${isMobileMenuOpen ? 'mobile-open' : ''}`}>
        <div className="nav-header">
          <h2>LoRaWAN Monitor</h2>
        </div>
        <ul className="nav-menu">
          {navItems.map(item => {
            const IconComponent = item.icon;
            return (
              <li key={item.id}>
                <button
                  className={`nav-item ${activeTab === item.id ? 'active' : ''}`}
                  onClick={() => {
                    setActiveTab(item.id);
                    setIsMobileMenuOpen(false);
                  }}
                >
                  <IconComponent className="nav-icon" />
                  <span>{item.label}</span>
                </button>
              </li>
            );
          })}
        </ul>
      </nav>

      {/* Mobile Overlay */}
      {isMobileMenuOpen && (
        <div 
          className="mobile-overlay"
          onClick={() => setIsMobileMenuOpen(false)}
        />
      )}
    </>
  );
};

// Dashboard Component (Your existing beacon monitor)
const Dashboard = () => {
  const [beacons, setBeacons] = useState([]);
  const [loading, setLoading] = useState(false);
  const [lastUpdate, setLastUpdate] = useState(null);
  const [timeRange, setTimeRange] = useState(2);

  // Mock data for demonstration
  const mockBeacons = [
    { mac_address: '0ae427c75e19', device_name: 'LW003-B(lab)', beacon_rssi: -70, last_seen: new Date().toISOString(), status: 'active' },
    { mac_address: '171a0308d27e', device_name: 'LW003-B(lab)', beacon_rssi: -60, last_seen: new Date().toISOString(), status: 'active' },
    { mac_address: '2fde47154aac', device_name: 'LW003-B(lab)', beacon_rssi: -74, last_seen: new Date().toISOString(), status: 'active' },
    { mac_address: '304a1d4e8a89', device_name: 'LW003-B(lab)', beacon_rssi: -84, last_seen: new Date().toISOString(), status: 'active' },
    { mac_address: '32f061fbf2aa', device_name: 'LW003-B(lab)', beacon_rssi: -66, last_seen: new Date().toISOString(), status: 'active' },
    { mac_address: '3ed81e3f9f7e', device_name: 'LW003-B(lab)', beacon_rssi: -77, last_seen: new Date().toISOString(), status: 'active' },
    { mac_address: '5b2b9fba6af4', device_name: 'LW003-B(lab)', beacon_rssi: -84, last_seen: new Date().toISOString(), status: 'active' },
    { mac_address: '661e3100eaec', device_name: 'LW003-B(lab)', beacon_rssi: -82, last_seen: new Date().toISOString(), status: 'active' },
    { mac_address: 'b49a95664821', device_name: 'LW003-B(lab)', beacon_rssi: -82, last_seen: new Date().toISOString(), status: 'active' },
    { mac_address: 'b49a95664830', device_name: 'LW003-B(lab)', beacon_rssi: -80, last_seen: new Date().toISOString(), status: 'active' },
    { mac_address: '7876d9160fc9', device_name: 'LW003-B(lab)', beacon_rssi: -91, last_seen: new Date().toISOString(), status: 'new' },
    { mac_address: '7c4d8fd0a4d0', device_name: 'LW003-B(lab)', beacon_rssi: -53, last_seen: new Date().toISOString(), status: 'active' },
  ];

  const fetchData = async () => {
    setLoading(true);
    try {
      // Simulate API call
      await new Promise(resolve => setTimeout(resolve, 1000));
      setBeacons(mockBeacons);
      setLastUpdate(new Date());
    } catch (error) {
      console.error("Error fetching beacon data:", error);
      setBeacons([]);
    }
    setLoading(false);
  };

  useEffect(() => {
    fetchData();
    const interval = setInterval(fetchData, 30000); // auto-refresh every 30 sec for demo
    return () => clearInterval(interval);
  }, [timeRange]);

  const getSignalStrengthClass = (rssi) => {
    if (rssi >= -45) return 'good';
    if (rssi >= -70) return 'warning';
    return 'critical';
  };

  const getStatusClass = (status) => {
    switch (status?.toLowerCase()) {
      case 'active':
        return 'active';
      case 'warning':
        return 'warning';
      case 'critical':
        return 'critical';
      case 'new':
        return 'new';
      default:
        return 'active';
    }
  };

  const formatDateTime = (dateString) => {
    if (!dateString) return 'N/A';
    try {
      const date = new Date(dateString);
      return date.toLocaleString();
    } catch {
      return dateString;
    }
  };

  const formatRSSI = (rssi) => {
    if (rssi === null || rssi === undefined) return 'N/A';
    return `${rssi} dBm`;
  };

  return (
    <div className="dashboard-content">
      <div className="dashboard-header">
        <h1>LoRaWAN Beacon Monitor</h1>
        {lastUpdate && (
          <p className="last-update">
            Last updated: {lastUpdate.toLocaleTimeString()}
          </p>
        )}
      </div>

      <div className="beacon-section">
        <div className="section-header">
          <h2>
            <FaTable className="section-icon" />
            Detected Beacons
          </h2>

          <div className="controls">
            <select 
              value={timeRange} 
              onChange={(e) => setTimeRange(Number(e.target.value))}
              className="time-select"
            >
              <option value={2}>Last 2 min</option>
              <option value={5}>Last 5 min</option>
              <option value={10}>Last 10 min</option>
            </select>

            <button 
              className="refresh-button" 
              onClick={fetchData}
              disabled={loading}
            >
              <FaSync className={loading ? 'spinning' : ''} />
              {loading ? 'Refreshing...' : 'Refresh'}
            </button>
          </div>
        </div>

        {beacons.length > 0 ? (
          <div className="table-container">
            <table className="beacon-table">
              <thead>
                <tr>
                  <th>Device</th>
                  <th>MAC Address</th>
                  <th>Signal Strength</th>
                  <th>Last Seen</th>
                  <th>Status</th>
                </tr>
              </thead>
              <tbody>
                {beacons.map((beacon, index) => (
                  <tr key={beacon.mac_address}>
                    <td>{beacon.device_name || `Beacon #${index + 1}`}</td>
                    <td className="mac-address">{beacon.mac_address}</td>
                    <td>
                      <span className={`signal-strength ${getSignalStrengthClass(beacon.beacon_rssi)}`}>
                        {formatRSSI(beacon.beacon_rssi)}
                      </span>
                    </td>
                    <td>{formatDateTime(beacon.last_seen)}</td>
                    <td>
                      <span className={`status ${getStatusClass(beacon.status)}`}>
                        {beacon.status}
                      </span>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        ) : (
          <div className="no-data">
            <FaTimesCircle className="no-data-icon" />
            <p>No beacon data available</p>
          </div>
        )}
      </div>
    </div>
  );
};

// Placeholder Components for other sections
const MapView = () => (
  <div className="page-content">
    <h1>Map View</h1>
    <div className="placeholder">
      <FaMap size={64} />
      <p>Interactive beacon map will be displayed here</p>
    </div>
  </div>
);

const Reports = () => (
  <div className="page-content">
    <h1>Reports</h1>
    <div className="placeholder">
      <FaChartBar size={64} />
      <p>Analytics and reports will be displayed here</p>
    </div>
  </div>
);

const Admin = () => (
  <div className="page-content">
    <h1>Admin Panel</h1>
    <div className="placeholder">
      <FaUserCog size={64} />
      <p>Administrative controls will be displayed here</p>
    </div>
  </div>
);

