        .refresh-button:hover:not(:disabled) {
          transform: translateY(-2px);
          box-shadow: 0 8px 25px rgba(102, 126, 234, 0.3);
        }

        .refresh-button:disabled {
          opacity: 0.7;
          cursor: not-allowed;
        }

        .spinning {
          animation: spin 1s linear infinite;
        }

        @keyframes spin {
          from { transform: rotate(0deg); }
          to { transform: rotate(360deg); }
        }

        .table-container {
          overflow-x: auto;
          border-radius: 12px;
          border: 1px solid #e2e8f0;
        }

        .beacon-table {
          width: 100%;
          border-collapse: collapse;
          background: white;
        }

        .beacon-table thead {
          background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
        }

        .beacon-table th {
          padding: 18px 16px;
          text-align: left;
          color: white;
          font-weight: 600;
          font-size: 14px;
          text-transform: uppercase;
          letter-spacing: 0.5px;
        }

        .beacon-table td {
          padding: 16px;
          border-bottom: 1px solid #f7fafc;
          color: #4a5568;
          vertical-align: middle;
        }

        .beacon-table tbody tr:hover {
          background: #f8faff;
        }

        .mac-address {
          font-family: 'Monaco', 'Menlo', monospace;
          font-size: 13px;
          color: #2d3748;
        }

        .signal-strength {
          display: inline-flex;
          align-items: center;
          padding: 6px 12px;
          border-radius: 20px;
          font-weight: 600;
          font-size: 13px;
        }

        .signal-strength::before {
          content: 'â—';
          margin-right: 6px;
          font-size: 12px;
        }

        .signal-strength.good {
          background: #c6f6d5;
          color: #22543d;
        }

        .signal-strength.good::before {
          color: #38a169;
        }

        .signal-strength.warning {
          background: #fef2d1;
          color: #975a16;
        }

        .signal-strength.warning::before {
          color: #d69e2e;
        }

        .signal-strength.critical {
          background: #fed7d7;
          color: #822727;
        }

        .signal-strength.critical::before {
          color: #e53e3e;
        }

        .status {
          display: inline-flex;
          align-items: center;
          padding: 6px 14px;
          border-radius: 20px;
          font-weight: 600;
          font-size: 12px;
          text-transform: uppercase;
          letter-spacing: 0.5px;
        }

        .status.active {
          background: #c6f6d5;
          color: #22543d;
        }

        .status.new {
          background: #bee3f8;
          color: #2a4a6b;
        }

        .status.warning {
          background: #fef2d1;
          color: #975a16;
        }

        .status.critical {
          background: #fed7d7;
          color: #822727;
        }

        .no-data {
          text-align: center;
          padding: 60px 20px;
          color: #a0aec0;
        }

        .no-data-icon {
          font-size: 3rem;
          margin-bottom: 15px;
          opacity: 0.6;
        }

        /* Placeholder Pages */
        .page-content {
          background: rgba(255, 255, 255, 0.95);
          backdrop-filter: blur(20px);
          margin: 20px;
          border-radius: 20px;
          padding: 30px;
          box-shadow: 0 10px 40px rgba(0, 0, 0, 0.1);
          min-height: calc(100vh - 40px);
        }

        .page-content h1 {
          background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
          -webkit-background-clip: text;
          -webkit-text-fill-color: transparent;
          background-clip: text;
          font-size: 2.5rem;
          font-weight: 700;
          margin-bottom: 40px;
          text-align: center;
        }

        .placeholder {
          text-align: center;
          padding: 80px 20px;
          color: #a0aec0;
        }

        .placeholder svg {
          opacity: 0.6;
          margin-bottom: 20px;
        }

        /* Mobile Responsiveness */
        @media (max-width: 768px) {
          .mobile-menu-toggle {
            display: block;
          }

          .navigation {
            transform: translateX(-100%);
          }

          .navigation.mobile-open {
            transform: translateX(0);
          }

          .mobile-overlay {
            display: block;
          }

          .main-content {
            margin-left: 0;
          }

          .dashboard-content,
          .page-content {
            margin: 10px;
            padding: 20px;
            border-radius: 15px;
          }

          .dashboard-header h1,
          .page-content h1 {
            font-size: 2rem;
          }

          .section-header {
            flex-direction: column;
            align-items: stretch;
            gap: 15px;
          }

          .controls {
            justify-content: space-between;
          }

          .beacon-table {
            font-size: 14px;
          }

          .beacon-table th,
          .beacon-table td {
            padding: 12px 8px;
          }
        }

        @media (max-width: 480px) {
          .dashboard-content,
          .page-content {
            margin: 5px;
            padding: 15px;
          }

          .beacon-table th,
          .beacon-table td {
            padding: 10px 6px;
            font-size: 12px;
          }

          .mac-address {
            font-size: 11px;
          }

          .signal-strength,
          .status {
            font-size: 11px;
            padding: 4px 8px;
          }
        }
      `}</style>
    </div>
  );
}

export default App;
